  /**
   * @ngdoc directive
   * @name mapsDirectives.directive:clock
   * @description
   * Directive responsible for showing current time
   */
  directives.directive('polokoClock', ['PATHS', '$rootScope', '$interval', function (PATHS, $rootScope, $interval) {
    return {
      restrict: 'A',
      templateUrl: PATHS.TEMPLATE_PATH + 'directives/clock.html',
      controller: ['$scope', function ($scope) {
        var setTime = function () {
          var date = new Date(),
            hours = date.getHours(),
            minutes = date.getMinutes();

          if (hours < 10) {
            hours = '0' + hours;
          }

          if (minutes < 10) {
            minutes = '0' + minutes;
          }

          if ($scope.hours != hours || $scope.minutes != minutes) {
            $scope.hours = hours;
            $scope.minutes = minutes;
            $rootScope.$broadcast('polokoTimeChanged', {hours: hours, minutes: minutes});
          }
        };

        setTime();
        $interval(setTime, 10000);
      }]
    };
  }]);

  /**
   * @ngdoc directive
   * @name mapsDirectives.directive:polokoMap
   * @description
   * Directive responsible for showing map
   */
  directives.directive('polokoMap', ['$interval', '$analytics', 'PATHS', 'CONFIG', 'MODEL', 'mapService', 'polokoService',
    function ($interval, $analytics, PATHS, CONFIG, MODEL, mapService, polokoService) {
      return {
        restrict: 'A',
        templateUrl: PATHS.TEMPLATE_PATH + 'directives/map.html',
        replace: true,
        scope: {
          markerList: '=',
          pathList: '=',
          layersList: '='
        },
        link: function ($scope, linkElement) {
          // Observe the element's width.
          $scope.$watch(
            function () {
              return linkElement ? linkElement.width() + " " + linkElement.height() : '';
            },
            function (newValue, oldValue) {
              if (newValue != oldValue) {
                mapService.getMap(function (map) {
                  map.invalidateSize();
                });
              }
            }
          );
        },
        controller: ['$scope', '$rootScope', '$timeout', '$translate', '$state', '$stateParams', 'MapModels',
          function ($scope, $rootScope, $timeout, $translate, $state, $stateParams, MapModels) {
            $scope.id = mapService.makeid();
            var extraMarkers = {};

            var setLayers = function () {
              $scope.layers = {
                baselayers: CONFIG.MAP.LAYERS.all($rootScope.language, $translate),
                overlays: {
                  extraMarkers: {type: 'group', name: 'extraMarkers', visible: true}
                }
              };

              if ($scope.layersList) {
                var baselayers = {};
                angular.forEach(Object.keys($scope.layersList), function (layerName) {
                  baselayers[layerName + $rootScope.language] = $scope.layersList[layerName]($rootScope.language, $translate);
                });
                $scope.layers.baselayers = baselayers;
              }
            };

            $timeout(function () {
              mapService.getMap(function (map) {
                map.invalidateSize();
              });
            });
            setLayers();

            $scope._coordinates = mapService.getBounds([]);
            $scope._center = {};

            var addExtraMarkers = function (markers) {
              for (var key in $scope._markers) {
                if($scope._markers[key].layer === 'extraMarkers') {
                  delete $scope._markers[key];
                }
              }
              extraMarkers = markers;
              $scope._markers = angular.extend({}, extraMarkers, $scope._markers);
              if (!$rootScope.$$phase) {
                $scope.$apply();
              }
            };

            var directiveService = new mapService.directiveService($scope.id, function (markers, center, bounds) {
              $scope._markers = angular.extend({}, extraMarkers, $scope.markerList, markers);

              // jeśli mamy wyszukiwanie i mamy tylko jeden punkt (start/koniec) to ma nie zmieniać położenia mapy
              if ($state.current.name.indexOf('poloko.search') === -1 || ($scope._markers && $scope._markers.start && $scope._markers.end)) {
                if (center) {
                  $scope._center = center;
                }

                $scope._coordinates = bounds;
              }
            });

            $scope.$on('revalidateMapBounds', function () {
              $timeout(function () {
                mapService.getMap(function (map) {
                  map.invalidateSize();
                  directiveService.setNewData($scope.centerMapOnLocation, $scope.markerList, $scope._center);
                });
              }, 500);
            });
            $scope.$watch('markerList', function () {
              directiveService.setNewData($scope.centerMapOnLocation, $scope.markerList, $scope._center);
            });
            $scope.$watch('markerList.length', function () {
              directiveService.setNewData($scope.centerMapOnLocation, $scope.markerList, $scope._center);
            });

            $scope.$on('leafletDirectiveMarker.dragend', function (event, args) {
              var marker = args.leafletEvent.target.getLatLng();
              $analytics.eventTrack('timetables-marker-dragged', {category: 'timetables'});

              $state.transitionTo('poloko.index.point', {lat: marker.lat, lng: marker.lng}, {inherit: true});
            });

            $scope.$watch('_center', function (newVal) {
              if (directiveService.center) {
                directiveService.center.zoom = newVal.zoom;
              }
              directiveService.loadExtraMarkers(newVal, $scope._coordinates, addExtraMarkers);
            }, true);

            $scope.centerMapOnLocation = false;
            $scope.toggleCenterMapOnLocation = function () {
              $scope.centerMapOnLocation = !$scope.centerMapOnLocation;
              directiveService.setNewData($scope.centerMapOnLocation, $scope.markerList, $scope._center);
            };
            var turnOffCenterMapOnLocation = function () {
              $scope.centerMapOnLocation = false;
              directiveService.centerOnLocation = $scope.centerMapOnLocation;
            };

            $scope.$on('leafletDirectiveMap.drag', turnOffCenterMapOnLocation);

            var options = {
              contextmenu: true,
              contextmenuWidth: 140,
              contextmenuItems: []
            };

            if (MODEL.HAS_ROUTING) {
              options.contextmenuItems.push({
                text: $translate.instant('CONTEXT_MENU.START'),
                callback: function (point) {
                  $state.transitionTo('poloko.search.results', {
                    startLat: polokoService.roundLocation(point.latlng.lat),
                    startLng: polokoService.roundLocation(point.latlng.lng)
                  }, {inherit: true});
                }
              }, {
                text: $translate.instant('CONTEXT_MENU.END'),
                callback: function (point) {
                  $state.transitionTo('poloko.search.results', {
                    endLat: polokoService.roundLocation(point.latlng.lat),
                    endLng: polokoService.roundLocation(point.latlng.lng)
                  }, {inherit: true});
                }
              });
              if ($state.current.name.indexOf('poloko.search') > -1) {
                options.contextmenuItems.push(
                  '-',
                  {
                    text: $translate.instant('CONTEXT_MENU.CLEAR_ALL'),
                    callback: function () {
                      $state.go('poloko.search', {
                        groupId: $stateParams.groupId,
                        viewType: $stateParams.viewType
                      }, {reload: true});
                    }
                  }
                );
              }
            }

            options.contextmenuItems.push(
              {
                text: $translate.instant('CONTEXT_MENU.SHOW_ALL'),
                callback: function () {
                  $scope._coordinates = mapService.getBounds($scope.markerList);
                }
              },
              '-',
              {
                text: '<span class="' + CONFIG.MAP.ICON_PREFIX + '-zoom-in"></span> ' + $translate.instant('CONTEXT_MENU.ZOOM_IN'),
                callback: function () {
                  mapService.getMap(function (map) {
                    map.zoomIn();
                  });
                }
              },
              {
                text: '<span class="' + CONFIG.MAP.ICON_PREFIX + '-zoom-out"></span> ' + $translate.instant('CONTEXT_MENU.ZOOM_OUT'),
                callback: function () {
                  mapService.getMap(function (map) {
                    map.zoomOut();
                  });
                }
              }
            );

            L.Map.mergeOptions(options);
          }]
      };
    }]);

