  /**
   * @ngdoc directive
   * @name mapsDirectives.directive:alerts
   * @description
   * Directive responsible for displaying
   */
  directives.directive('polokoAlerts', ['PATHS', function (PATHS) {
    return {
      restrict: 'A',
      scope: {},
      controller: ['$scope', '$timeout', function ($scope, $timeout) {
        $scope.alerts = [];
        $scope.lastId = 0;

        $scope.$on('newAlert', function (event, alert) {
          $scope.lastId += 1;
          alert.id = $scope.lastId;
          $scope.alerts.push(alert);
          $timeout(function () {

            $scope.alerts.splice($scope.alerts.indexOf(alert), 1);
          }, 4000);
        });
      }],
      templateUrl: PATHS.TEMPLATE_PATH + 'directives/alerts.html'
    };
  }]);

