  /**
   * @ngdoc directive
   * @name mapsDirectives.directive:polokoSelector
   * @description
   * Selects agency, search for stop and route
   */
  directives.directive('polokoSelector', ['PATHS', function (PATHS) {
    return {
      restrict: 'A',
      scope: {},
      controller: ['$scope', '$state', '$stateParams', 'polokoService', '$analytics', 'ipCookie',
        function ($scope, $state, $stateParams, polokoService, $analytics, ipCookie) {
          $scope.imageUrl = PATHS.IMAGE;

          polokoService.getAgencies(function (data) {
            $scope.agencies = data.agencies;
            $scope.groups = data.groups;
            $scope.groupList = data.groupList;

            polokoService.getAgency($stateParams.groupId, $stateParams.viewType, function (agency) {
              $scope.agency = {obj: agency, viewType: 'agency'};
            });
          });

          $scope.setAgency = function (item, model) {
            var stateName = 'poloko.index.locate';
            if ($state.current.name.indexOf('poloko.search') > -1) {
              stateName = 'poloko.search';
            }

            if (model) {
              $analytics.eventTrack('agency-changed', {category: 'timetables', label: model.type});
              ipCookie('agency', {groupId: model.obj.id, viewType: model.type});
              $state.go(stateName, {groupId: model.obj.id, viewType: model.type});
            } else if ($stateParams.groupId != undefined) {
              $state.go(stateName);
            }
          };

          $scope.getAgencyName = polokoService.getAgencyName;

          $scope.getSearchResults = function (typedValue, callback) {
            return polokoService.getSearchResults($stateParams.groupId, $stateParams.viewType, typedValue.typedValue, callback);
          };

          $scope.selectedSearchResult = function (result) {
            polokoService.saveSearchResults(result);

            if (result.resultType === 'route') {
              $state.transitionTo('poloko.route', {
                groupId: $stateParams.groupId,
                viewType: $stateParams.viewType,
                routeId: result.id,
                direction: result.direction
              });
            } else if (result.resultType === 'stop') {
              $state.transitionTo('poloko.singlestop.departures', {
                groupId: $stateParams.groupId,
                viewType: $stateParams.viewType,
                stopId: result.id
              });
            }

          };

          $scope.formatSearchResult = function (obj) {
            if (!obj) {
              return '';
            }
            var icon = 'marker';
            var name = obj.name;

            if (obj.resultType === 'route') {
              icon = obj.routeType.toLowerCase();
              name += '<span class="info"> &rarr; ' + obj.lastStop;
              if ($scope.getAgencyName(obj.agencyId)) {
                name += ' (' + $scope.getAgencyName(obj.agencyId) + ')';
              }
              name += '</span>';
            } else if (obj.resultType === 'stop') {
              name += '<span class="info"> ' + obj.routeNames.join(', ') + '</span>';
            }

            return '<div><span class="search-result-icon poloko-icon-' + icon + '"></span>' + name + '</div>';
          };
        }],
      templateUrl: PATHS.TEMPLATE_PATH + 'directives/selector.html'
    };
  }]);

  /**
   * @ngdoc directive
   * @name mapsDirectives.directive:polokoRouteDetails
   * @description
   * Directive responsible for displaying details for route: name, headsign, list of stops
   */
  directives.directive('polokoRouteDetails', ['PATHS', function (PATHS) {
    return {
      restrict: 'A',
      scope: {
        route: '=',
        stop: '=',
        tripTime: '='
      },
      controller: ['$scope', '$timeout', '$stateParams', '$state', '$filter', 'MODEL',
        function ($scope, $timeout, $stateParams, $state, $filter, MODEL) {
          $scope.humanizeTimeToStop = function (minutes) {
            var polokoTime = $filter('polokoTime');
            return polokoTime({minutes: minutes});
          };

          $scope.direction = $stateParams.direction;
          $scope.directions = ['TO', 'FROM'];

          $scope.HAS_ROUTING = MODEL.HAS_ROUTING;

          $scope.allStopsVisible = false;
          $scope.activeStopIndex = -1;
          if ($scope.route) {
            for (var i = 0; i < $scope.route.stops.length; i++) {
              if ($scope.route.stops[i].id == $stateParams.stopId) {
                $scope.activeStopIndex = i;
                break;
              }
            }
          }

          $scope.setStopVisible = function (visibility) {
            $scope.allStopsVisible = visibility;
          };
        }],
      templateUrl: PATHS.TEMPLATE_PATH + 'directives/routeDetails.html'
    };
  }]);

  /**
   * @ngdoc directive
   * @name mapsDirectives.directive:polokoLoaderError
   * @description
   * Directive responsible for displaying details for route: name, headsign, list of stops
   */
  directives.directive('polokoLoaderError', ['PATHS', '$translate', function (PATHS, $translate) {
    return {
      restrict: 'A',
      scope: {
        transitionError: '='
      },
      controller: ['$scope', '$state', '$rootScope', function ($scope, $state, $rootScope) {
        $scope.$watch('transitionError', function () {
          if ($state.current.name || $rootScope.transitionError === '404') {
            $scope.showCloseButton = true;
          } else {
            $scope.showCloseButton = false;
          }
        });

        $scope.closeErrorModal = function () {
          $rootScope.loading = false;
          $rootScope.transitionError = '';
        };
      }],
      templateUrl: PATHS.TEMPLATE_PATH + 'directives/loader-error.html'
    }
  }]);

