(function () {
  

  var formsDirectives = angular.module('poloko.directives');

  /**
   * @ngdoc directive
   * @name mapsDirectives.directive:polokoInput
   */
  formsDirectives.directive('polokoInput', ['PATHS', function (PATHS) {
    return {
      restrict: 'A',
      replace: true,
      scope: {
        model: '=',
        name: '@',
        label: '@',
        inputType: '@'
      },
      templateUrl: PATHS.TEMPLATE_PATH + 'directives/input.html'
    };
  }]);

  /**
   * @ngdoc directive
   * @name mapsDirectives.directive:polokoTypeahead
   */
  formsDirectives.directive('polokoTypeahead', ['PATHS', function (PATHS) {
    return {
      restrict: 'A',
      replace: true,
      scope: {
        setModel: '&',
        name: '@',
        label: '@',
        getList: '&',
        format: '&',
        placeholder: '@',
        model: '='
      },
      templateUrl: PATHS.TEMPLATE_PATH + 'directives/typeahead.html',
      controller: ['$scope', '$analytics', function ($scope, $analytics) {
        $scope.loadingResults = false;

        $scope._getList = function (typedValue) {
          $scope.loadingResults = true;

          if (typedValue.typedValue) {
            $analytics.eventTrack('autocomplete-search', {category: 'common', label: typedValue.typedValue});
          }

          $scope.getList({
            typedValue: typedValue,
            callback: function (results) {
              $scope.list = results;
              $scope.loadingResults = false;
            }
          });
        };
      }]
    };
  }]);
}());
