(function () {
  

  angular.module('poloko.errors', [])
    .service("errors", ['$rootScope', '$timeout', '$log', '$translate', function ($rootScope, $timeout, $log, $translate) {
      $rootScope.alerts = new Array();

      var addError = function (error, silent) {
        silent = angular.isDefined(silent) ? silent : false;
        adminErrorMessage(error);
        if (!silent) {
          userErrorMessage(error);
        }
      };

      var addTransitionError = function (error) {
        $rootScope.transitionError = errorMsg(error.status);
      };

      var addAlert = function (alert, permanent) {
        permanent = (angular.isDefined(permanent) ? permanent : false);
        var length = $rootScope.alerts.push(alert);
        if (!permanent) {
          $timeout(function () {
            closeAlert(length - 1);
          }, 3000);
        }
      };

      var closeAlert = function (index) {
        $rootScope.alerts.splice(index, 1);
      };

      var adminErrorMessage = function (error) {
        var errorInfo = '[ERROR] ';
        if (angular.isDefined(error.reason)) {
          if (angular.isDefined(error.reason.status)) {
            errorInfo += error.reason.status + ' - ' + error.reason.statusText;
          }
          if (angular.isDefined(error.reason.message)) {
            errorInfo += error.reason.message;
          }
        }
        $log.error(errorInfo + ': ' + error.message, error);
      };

      var errorMsg = function (status) {
        var msg;
        if (angular.isDefined(status)) {
          switch (status) {
          case 403:
            msg = 'ERRORS.403';
            break;
          case 404:
            msg = '404';
            break;
          case 500:
          case 502:
          case 504:
          default:
            msg = 'ERRORS.TIMEOUT';
          }
        } else {
          msg = 'ERRORS.TIMEOUT';
        }
        return $translate.instant(msg);
      };

      var userErrorMessage = function (error) {
        if (error.message !== "Exception" && angular.isDefined(error.reason)) {
          $rootScope.$broadcast('newAlert', {msg: errorMsg(error.reason), type: 'alert'}, true);
        }
      };

      return {
        catch: function (message) {
          return function (reason) {
            addError({message: message, reason: reason});
          };
        },
        error: function (error, message, silent) {
          addError({message: message, reason: error}, silent);
        },
        transitionError: function (message) {
          addTransitionError(message);
        }
      };
    }]);

}());
