(function () {
  

  var prettyDuration = function (duration, $translate) {
    var result;
    if (duration.asMinutes() > 60 && duration.asMinutes() % 60 != 0) {
      result = $translate.instant('TIME.WITH_HOURS', {hours: duration.hours(), minutes: duration.minutes()});
    } else {
      result = duration.humanize();
    }
    return result;
  };

  angular.module('poloko.filters', ['ngSanitize'])
    .filter('polokoTime', ['$translate', 'moment', function ($translate, moment) {
      return function (time) {
        var duration = moment.duration(time);
        return prettyDuration(duration, $translate);
      };
    }])
    .filter('polokoDuration', ['$translate', 'moment', function ($translate, moment) {
      return function (from, to) {
        var duration = moment.duration(from).subtract(to);
        return prettyDuration(duration, $translate);
      };
    }])
    .filter('trusted', ['$sce', function ($sce) {
      return function (url) {
        return $sce.trustAsResourceUrl(url);
      };
    }])
    .filter('countTime', ['$translate', 'moment', function ($translate, moment) {
      return function (date) {
        var result;
        if (date) {
          date = moment(date, 'HH:mm');
          var diff = moment().diff(date, 'minutes');
          if (diff < -20) {
            result = $translate.instant('STOP.DEPARTURE_AT', {time: date.format('HH:mm')});
          } else if (diff > 0) {
            result = date.add(1, 'd').from(moment());
          } else {
            result = date.from(moment());
          }
        } else {
          result = '';
        }
        return result;
      };
    }])
    .filter('leadingZero', function () {
      return function (number, length) {
        if (!number && number != 0) {
          return '';
        }
        if (!length) {
          length = 2;
        }
        return (1e4 + number + '').slice(-length);
      };
    })
    .filter('alphanumOrderBy', function () {
      return function (list, property) {
        return list.sort(function (a, b) {
          return alphanumCase(a[property], b[property]);
        });
      };
    })
    .filter('hours', function () {
      return function (hours) {
        return hours % 24;
      };
    });

}());
