services.service('timetableService', ['$q', '$filter', 'polokoHttp', 'ipCookie', 'errors', 'polokoService', 'PATHS', 'CONFIG', 'moment',
  function ($q, $filter, polokoHttp, ipCookie, errors, polokoService, PATHS, CONFIG, moment) {
    var weekDays = CONFIG.WEEKDAYS;
    var services = {
      getRouteForStopAndTrip: function (routeId, direction, stopId, tripId) {
        return polokoHttp.post(PATHS.POLOKO_API + 'timetables/routeForStopAndTrip', {
          routeId: routeId,
          stopId: stopId,
          direction: direction,
          tripId: tripId
        });
      },
      getTimetables: function (routeId, direction, stopId, fromDate, toDate, callback, error) {
        polokoHttp.post(PATHS.POLOKO_API + 'timetables/timetable', {
          routeId: routeId,
          direction: direction,
          stopId: stopId,
          fromDate: $filter('date')(fromDate, CONFIG.DATE_FORMAT),
          toDate: $filter('date')(toDate, CONFIG.DATE_FORMAT)
        })
          .then(function (data) {
            if (!data || data.groups.length === 0 || !data.groups[0]) {
              errors.transitionError(error);
              return;
            }
            var maxTableColumns = 1;

            var weekday = CONFIG.WEEKDAYS[moment().weekday()];

            /*
             Sortowanie minut dla każdej godziny,
             Liczenie maksymalnej liczby kolumn,
             Grupowanie dni w grupy
             Liczenie dat dla grup
             */
            for (var i = 0; i < data.groups.length; i++) {
              var hours = data.groups[i].trips.hours;

              var monday = fromDate;

              if (data.groups[i].days.indexOf(weekday) > -1) {
                data.calendarId = i;
              }

              if (data.groups[i].days.length > 1) {

                data.groups[i].days = [data.groups[i].days[0], data.groups[i].days[data.groups[i].days.length - 1]];

                var weekDayIndex1 = weekDays.indexOf(data.groups[i].days[0]) + 1;
                var weekDayIndex2 = weekDays.indexOf(data.groups[i].days[1]) + 1;
                data.groups[i].dates = [moment(monday).day(weekDayIndex1).toDate(), moment(monday).day(weekDayIndex2).toDate()];
              } else {
                var weekDayIndex = weekDays.indexOf(data.groups[i].days[0]) + 1;
                data.groups[i].dates = [moment(monday).day(weekDayIndex).toDate()];
              }
              for (var hour in hours) {
                hours[hour] = $filter('orderBy')(hours[hour], 'minutes');
                if (hours[hour].length > maxTableColumns) {
                  maxTableColumns = hours[hour].length;
                }
              }
            }

            if(!data.calendarId) {
              data.calendarId = 0;
            }

            data.maxTableColumns = maxTableColumns;
            data.selectedRange = [fromDate, toDate];

            callback(data);
          })
          .catch(error);
      },
      setClosestDepartureTime: function (calendarId, calendars) {
        var date = new Date();
        return services.findClosestDepartureTime({
          hours: date.getHours(),
          minutes: date.getMinutes()
        }, calendarId, calendars);
      },
      findClosestDepartureTime: function (time, calendarId, calendars) {
        if (!calendars[calendarId]) return {};

        var createTrip = function (tripId, hours, minutes) {
          return {tripId: tripId, time: {hours: hours, minutes: minutes}};
        };

        // check same hour (also check if minutes is bigger)
        var minutes = calendars[calendarId].trips.hours[time.hours];
        if (minutes) {
          for (var i = 0; i < minutes.length; i++) {
            if (minutes[i].minutes > time.minutes) {
              return createTrip(minutes[i].tripId, time.hours, minutes[i].minutes);
            }
          }
        }

        // check if there is any departure on the same day
        for (var hour = time.hours + 1; hour < 24; hour++) {
          minutes = calendars[calendarId].trips.hours[hour];
          if (minutes) {
            for (var i = 0; i < minutes.length; i++) {
              return createTrip(minutes[i].tripId, hour, minutes[i].minutes);
            }
          }
        }

        // start from beginning (departure on next day)
        for (var hour = 0; hour < time.hours + 1; hour++) {
          minutes = calendars[calendarId].trips.hours[hour];
          if (minutes) {
            for (var i = 0; i < minutes.length; i++) {
              return createTrip(minutes[i].tripId, hour, minutes[i].minutes);
            }
          }
        }
      },
      getTrip: function (tripId, dayGroups) {
        for (var i in dayGroups) {
          for (var j in dayGroups[i].trips.hours) {
            var hour = dayGroups[i].trips.hours[j];
            for (var k = 0; k < hour.length; k++) {
              if (hour[k].tripId == tripId) {
                return {tripId: hour[k].tripId, time: {hours: j, minutes: hour[k].minutes}};
              }
            }
          }
        }
      }
    };

    return services;

  }]);

