(function () {
  

  var services = angular.module('poloko.search.services', []);

  services.service('searchService', ['$q', '$filter', '$http', 'polokoHttp', '$translate', 'ipCookie', 'errors', 'PATHS', 'CONFIG', 'mapService', 'polokoService', 'MapModels',
    function ($q, $filter, $http, polokoHttp, $translate, ipCookie, errors, PATHS, CONFIG, mapService, polokoService, MapModels) {

      var searchServices = {};

      /**
       * @ngdoc function
       * @name autocompleteForCurrentMapBounds
       * @methodOf seachService.service:autocompleteForCurrentMapBounds
       * @param {string} val query value
       * @param {function} callback results fetcher
       *
       * @description
       * Get autocomplete suggestions from Poloko API, Places API and Geocode API using current map bounds.
       */
      searchServices.autocompleteForCurrentMapBounds = function (val, callback) {
        var service = this;

        var fallback = setTimeout(function () {
          fallback = null;
          service.autocomplete(val.typedValue, null, callback);
        }, 250);

        mapService.getMapBounds(function (mapBounds) {
          if (!fallback || !mapBounds) {
            return;
          }
          clearTimeout(fallback);
          service.autocomplete(val.typedValue, mapBounds, callback);
        });
      };

      /**
       * @ngdoc function
       * @name autocomplete
       * @methodOf seachService.service:autocomplete
       * @param {string} val query value
       * @param {object} mapBounds map bounds
       * @param {function} callback results fetcher
       *
       * @description
       * Get autocomplete suggestions from Poloko API, Places API and Geocode API.
       */
      searchServices.autocomplete = function (val, mapBounds, callback) {
        var service = this;
        var titles = [];
        var stopsResult = [];
        var placesResult = [];
        var addressesResult = [];

        if (!val || (typeof val) != "string" || val.length == 0) {
          var result = [];

          if (ipCookie('lastLocationSearch')) {
            result = ipCookie('lastLocationSearch');
          }

          // jeśli mamy aktualną pozycję to ją dodajmy na sam początek listy
          var userPosition = mapService.getCurrentUserPosition();
          if (userPosition) {
            result.splice(0, 0, {
              name: $translate.instant('YOUR_LOCALIZATION'),
              lat: userPosition.lat,
              lng: userPosition.lng,
              type: 'marker'
            });
          }

          callback(result);
          return;
        }

        var validateResultItem = function (item, theItem) {
          if (item.category && (item.category.id == 'city-town-village' || item.category.id == 'administrative-region')) {
            return false;
          }

          var distanceThreshold = Math.abs(mapBounds.getEast() - mapBounds.getWest()) * 2;
          var dLat = mapBounds.getCenter().lat - theItem.lat;
          var dLng = mapBounds.getCenter().lng - theItem.lng;
          if (Math.sqrt(dLat * dLat + dLng * dLng) > distanceThreshold) {
            return false;
          }

          if (titles.indexOf(theItem.name.toLowerCase()) != -1) {
            return false;
          }

          if (item.matchLevel && CONFIG.GEOCODE_ALLOWED_MATCH_LEVELS.indexOf(item.matchLevel) == -1) {
            return false;
          }

          return true;
        };

        var counter = 2;

        var processAll = function () {
          if (counter > 0) {
            // jeszcze nie wszystkie odpowiedzi przyszły
            counter -= 1;
          } else {
            var results = stopsResult.concat(placesResult, addressesResult);
            callback(results.slice(0, 20));
          }
        };

        polokoHttp.post(PATHS.POLOKO_SERVICE + 'search', val).then(function (response) {
          angular.forEach(response, function (item) {
            item.type = 'marker';
            stopsResult.push(item);
          });

          processAll();

        }).catch(function (errorCallback) {
          processAll();
          errors.catch("Could not reach POLOKO autocomplete service")(errorCallback);
        });

        if (!mapBounds || (typeof mapBounds.toBBoxString) != 'function') {
          return;
        }

        service.placeAutocompletePromise(val, mapBounds.toBBoxString()).then(function (response) {
          angular.forEach(response.data.results.items, function (item) {

            var theItem = {
              name: item.title + (item.vicinity ? ' (' + item.vicinity + ')' : ''),
              lat: item.position[0],
              lng: item.position[1],
              type: 'place'
            };

            if (validateResultItem(item, theItem, mapBounds)) {
              titles.push(theItem.name.toLowerCase());
              placesResult.push(theItem);
            }
          });

          processAll();
        }).catch(function (errorCallback) {
          processAll();
          errors.catch("Could not reach PLACES autocomplete service")(errorCallback);
        });

        service.addressAutocompletePromise(mapBounds, val).then(function (response) {
          if (response.data.response.view && response.data.response.view.length) {
            angular.forEach(response.data.response.view[0].result, function (item) {


              var theItem = {
                name: item.location.address.city ? item.location.address.street + ' (' + (item.location.address.postalCode ? item.location.address.postalCode + ' ' : '') + item.location.address.city + ')' : item.location.address.label,
                lat: item.location.displayPosition.latitude,
                lng: item.location.displayPosition.longitude,
                type: 'address'
              };

              if (validateResultItem(item, theItem, mapBounds)) {
                titles.push(theItem.name.toLowerCase());
                addressesResult.push(theItem);
              }
            });
          }

          processAll();
        }).catch(function (errorCallback) {
          processAll();
          errors.catch("Could not reach GEOCODING autocomplete service")(errorCallback);
        });
      };

      searchServices.saveSearchLocation = function (location) {
        var lastSearch = ipCookie('lastLocationSearch');
        var found = false;
        location.lat = polokoService.roundLocation(location.lat);
        location.lng = polokoService.roundLocation(location.lng);
        if (lastSearch) {
          for (var i = 0; i < lastSearch.length; i++) {
            if (lastSearch[i].lat === location.lat && lastSearch[i].lng === location.lng) {
              found = true;
              break;
            }
          }
        } else {
          lastSearch = [];
        }

        if (!found) {
          lastSearch.push(location);
        }

        if (lastSearch.length > 10) {
          lastSearch = lastSearch.slice(-10);
        }

        ipCookie('lastLocationSearch', lastSearch);
      };

      /**
       * @ngdoc function
       * @name placeAutocompletePromise
       * @methodOf mapsServices.service:placeAutocompletePromise
       * @param {string} query query value
       * @param {string} mapBBoxString map bounds as string
       *
       * @description
       * Get promise for Places API.
       */
      searchServices.placeAutocompletePromise = function (query, mapBBoxString) {
        return $http.get(PATHS.PLACES_API, {
          params: {
            'app_id': CONFIG.HERE_APP_ID,
            'app_code': CONFIG.HERE_APP_CODE,
            'tf': 'plain',
            'refinements': 'true',
            'q': query
          },
          headers: {
            'X-Map-Viewport': mapBBoxString
          }
        });
      };

      /**
       * @ngdoc function
       * @name addressAutocompletePromise
       * @methodOf mapsServices.service:placeAutocompletePromise
       * @param {object} mapBounds map bounds
       * @param {string} searchText query value
       *
       * @description
       * Get promise for Geolocation API.
       */
      searchServices.addressAutocompletePromise = function (mapBounds, searchText) {
        return $http.get(PATHS.GEOCODE_API, {
          params: {
            'app_id': CONFIG.HERE_APP_ID,
            'app_code': CONFIG.HERE_APP_CODE,
            'gen': '8',
            'locationattributes': 'none,address',
            'addressattributes': 'none,county,state,city,postalCode,street,houseNumber',
            'jsonattributes': '1',
            'maxresults': '10',
            'pageinformation': '1',
            'bbox': mapBounds.getNorth() + ',' + mapBounds.getWest() + ';' + mapBounds.getSouth() + ',' + mapBounds.getEast(),
            'searchText': searchText
          }
        });
      };

      /**
       * @ngdoc function
       * @name reverseGeocodePromise
       * @methodOf mapsServices.service:reverseGeocodePromise
       * @param {float} lat
       * @param {float} lng
       *
       * @description
       * Get promise for Reverse Geocode API.
       */
      searchServices.reverseGeocodePromise = function (lat, lng) {
        var defer = $q.defer();
        $http.get(PATHS.REVERSE_GEOCODE_API, {
          params: {
            'app_id': CONFIG.HERE_APP_ID,
            'app_code': CONFIG.HERE_APP_CODE,
            'maxresults': 1,
            'gen': 6,
            'mode': 'retrieveAddresses',
            'prox': lat + ',' + lng + ',150'
          }
        }).success(function (data) {
          var results = data.Response.View;
          if (!results || results.length < 1) {
            defer.reject();
            return;
          }

          results = results[0].Result;
          if (!results || results.length < 1) {
            defer.reject();
            return;
          }

          defer.resolve(results[0].Location.Address.Label);
        });
        return defer.promise;
      };

      searchServices.getMapBoundaries = function (agencyIds, callback, errorCallback) {
        polokoHttp.request({
          method: 'POST',
          url: PATHS.POLOKO_SERVICE + 'mapBoundaries',
          data: agencyIds
        }).then(function (data) {
          callback(data);
        })
            .error(errorCallback);
      };

      /**
       * @ngdoc function
       * @name getTransitRoutes
       * @methodOf mapsServices.service:polokoRoute
       * @param {object} data data to be sent
       * @param {function} callback callback function
       *
       * @description
       * Get transit routes from Poloko API.
       */
      searchServices.getTransitRoutes = function (route, callback, errorCallback) {
        var routeType = 'departure';
        if (route.type == CONFIG.ROUTING.TYPES.ARRIVAL) {
          routeType = 'arrival';
        }

        var query = {
          dateTime: route.date.value + ' ' + route.time,
          from: {
            lat: route.start.lat,
            lng: route.start.lng
          },
          to: {
            lat: route.end.lat,
            lng: route.end.lng
          },
          routingType: route.option.value
        };

        var regularValues = ['date', 'end', 'option', 'start', 'time', 'type'];

        for(var value in route) {
          if(regularValues.indexOf(value) < 0) {
            query[value] = route[value];
          }
        }

        polokoHttp.request({
          method: 'POST',
          url: PATHS.POLOKO_SERVICE + routeType,
          data: query
        }).then(function (data) {
          data.routes = data.routesList;
          callback(data);
        });
      };

      searchServices.processedPoint = function (obj, name) {
        this.obj = obj;
        this.name = name;
        var that = this;

        var latLngRegex = /^(\-?\d+\.\d+?),\s*(\-?\d+\.\d+?)$/;

        this.geocode = function () {
          if (this.obj[this.name] && this.obj[this.name].name) {
            var match = this.obj[this.name].name.match(latLngRegex);
            if (match && match.length === 3) {
              var lat = match[1];
              var lng = match[2];
              searchServices.reverseGeocodePromise(lat, lng)
                  .then(function (label) {
                    that.obj[that.name].name = label;
                  }), (function () {
              });
            }
          }
        };

        this.setUp = function (lat, lng, name) {
          if(!this.obj[this.name]) {
            this.obj[this.name] = {};
          }

          this.obj[this.name].lat = lat;
          this.obj[this.name].lng = lng;
          if (!name) {
            this.obj[this.name].name = lat + ', ' + lng;
            this.geocode();
          } else {
            this.obj[this.name].name = name;
          }
        };

        this.getMarker = function () {
          var markerType = CONFIG.MAP.TYPE.START;

          if(name === 'end') {
            markerType = CONFIG.MAP.TYPE.END;
          }

          var marker = new MapModels.Marker(this.obj[this.name].lat, this.obj[this.name].lng, markerType, this.obj[this.name].correctName);
          marker.draggable = true;

          return marker;
        }
      };

      return searchServices;
    }]);
}());
