(function () {
  
  var controllers = angular.module('poloko.search.controllers', []);

  controllers.controller('SearchController', ['$scope', '$rootScope', '$stateParams', '$state', '$analytics', 'mapService',
    'PageService', 'CONFIG', 'polokoService', 'moment',
    function ($scope, $rootScope, $stateParams, $state, $analytics, mapService, PageService, CONFIG, polokoService, moment) {
      $rootScope.$watch('mapData.markers', function (markers) {
        if (markers && markers.length > 0) {
          $scope.mapData.bounds = mapService.getBounds(markers);
        }
      });

      $scope.setPanelVisibility = function (panel, results) {
        $scope.resultsVisible = results;
        $scope.$emit('panel-visible-changed', panel);
      };

      $scope.setAvailableRoutes = function (routes) {
        $scope.availableRoutes = routes;
      };

      $scope.setActiveRoute = function (index) {
        $analytics.eventTrack('route-search-change-active-route', {category: 'route-search'});
        $scope.activeRoute = index;
        $scope.mapData = mapService.newRouteSearchResult($scope.availableRoutes, $scope.activeRoute);

        window.transitart.fire('poloko-after-load-route', {routeParams: $scope.route, routes: $scope.availableRoutes, activeRoute: index});
      };

      // Handle clicking on alternative routes - activate the clicked path
      $rootScope.$on('leafletDirectivePath.click', function (event, args) {
        var pathOptions = args.leafletEvent.target.options;
        if (angular.isDefined(pathOptions) && pathOptions.isAlternative) {
          $scope.setActiveRoute(pathOptions.index);
        }
      });

      $scope.setUpScope = function () {
        $scope.mapData = {};
        $scope.mapData.markers = {};
        $scope.mapData.paths = {};
        $scope.availableRoutes = undefined;
        $scope.route = {};
        $scope.activeRoute;

        $scope.dates = [];
        for (var i = 0; i < CONFIG.DAYS_AVAILABLE; i++) {
          var format = moment().add(i, 'days').format(CONFIG.MOMENT_DATE_FORMAT);
          $scope.dates.push({
            name: format,
            value: format
          });
        }
        $scope.dates[0].name = 'DATE.TODAY';
        $scope.dates[1].name = 'DATE.TOMORROW';

        $scope.routeOptions = [
          {name: 'SEARCH_FORM.VALUE.OPTIMAL', value: CONFIG.ROUTING.OPTIONS.OPTIMAL},
          {name: 'SEARCH_FORM.VALUE.MIN_CHANGE', value: CONFIG.ROUTING.OPTIONS.MIN_CHANGE},
          {name: 'SEARCH_FORM.VALUE.MIN_WALK', value: CONFIG.ROUTING.OPTIONS.MIN_WALK}
        ];

        // Init route params
        var d = new Date();
        $scope.route.time = moment().format(CONFIG.TIME_FORMAT);
        $scope.route.date = $scope.dates[0];
        $scope.route.type = CONFIG.ROUTING.TYPES.DEPARTURE;
        $scope.route.option = $scope.routeOptions[0];

        window.transitart.fire('poloko-route-search-is-setup', {route: $scope.route, dates: $scope.dates, options: $scope.routeOptions});
      };
      $scope.setUpScope();
      PageService.setMetaTags();
      $scope.routeIsLoading = false;
    }]);

  controllers.controller('SearchResultsController', ['$scope', '$rootScope', '$stateParams', '$state', '$timeout', '$analytics', 'mapService', 'searchService', 'polokoService', 'MapModels', 'PageService', 'CONFIG', 'errors', 'moment',
    function ($scope, $rootScope, $stateParams, $state, $timeout, $analytics, mapService, searchService, polokoService, MapModels, PageService, CONFIG, errors, moment) {
      $scope.showNextRoutes = function () {
        $analytics.eventTrack('route-search-next-time', {category: 'route-search'});
        $scope.route.type = CONFIG.ROUTING.TYPES.DEPARTURE;
        var lastDepartureTime = $scope.alternativeRoutesTimes.next.time;
        $scope.route.time = lastDepartureTime;
        $scope.$parent.changeTime();
      };

      $scope.showPrevRoutes = function () {
        $analytics.eventTrack('route-search-previous-time', {category: 'route-search'});
        $scope.route.type = CONFIG.ROUTING.TYPES.ARRIVAL;
        var prevDepartureTime = $scope.alternativeRoutesTimes.prev.time;
        $scope.route.time = prevDepartureTime;
        $scope.$parent.changeTime();
      };

      $scope.mapData.markers = {};
      $scope.mapData.paths = {};

      var startPoint = new searchService.processedPoint($scope.route, 'start');

      var endPoint = new searchService.processedPoint($scope.route, 'end');


      if ($stateParams.startLat && $stateParams.startLng) {
        startPoint.setUp($stateParams.startLat, $stateParams.startLng, $stateParams.startName);
        $scope.mapData.markers.start = startPoint.getMarker();
      }
      if ($stateParams.endLat && $stateParams.endLng) {
        endPoint.setUp($stateParams.endLat, $stateParams.endLng, $stateParams.endName);
        $scope.mapData.markers.end = endPoint.getMarker();
      }

      // przeciaganie markerow na mapie:
      $rootScope.$on('leafletDirectiveMarker.dragend', function (e, leafletEvent) {
        $analytics.eventTrack('route-search-marker-dragged', {category: 'route-search'});
        if (leafletEvent.modelName === 'start') {
          $state.transitionTo('poloko.search.results', {
            startLat: polokoService.roundLocation(leafletEvent.leafletEvent.target._latlng.lat),
            startLng: polokoService.roundLocation(leafletEvent.leafletEvent.target._latlng.lng),
            startName: ''
          }, {inherit: true});
        } else if (leafletEvent.modelName === 'end') {
          $state.transitionTo('poloko.search.results', {
            endLat: polokoService.roundLocation(leafletEvent.leafletEvent.target._latlng.lat),
            endLng: polokoService.roundLocation(leafletEvent.leafletEvent.target._latlng.lng),
            endName: ''
          }, {inherit: true});
        }
      });

      var setUpRoute = function () {
        if (angular.isDefined($scope.route) && $scope.route.start && $scope.route.start.lat && $scope.route.start.lng && $scope.route.end && $scope.route.end.lat && $scope.route.end.lng) {

          $scope.$parent.routeIsLoading = true;
          searchService.getTransitRoutes($scope.route, function (data) {
            $scope.routeIsLoading = true;
            $scope.alternativeRoutesTimes = {
              next: {time: data.nextTime, date: data.nextDate},
              prev: {time: data.previousArrivalTime, date: data.previousArrivalDate}
            };
            $scope.setAvailableRoutes(data.routes);
            if (angular.isDefined($scope.availableRoutes) && $scope.availableRoutes && $scope.availableRoutes.length > 0) {
              $scope.setActiveRoute(0);
            }
            $scope.setPanelVisibility('right', 'list');
            $scope.$parent.routeIsLoading = false;
          }, function (error) {
            errors.error(error, "Could not reach POLOKO getTransitRoutes");
            $scope.routeIsLoading = false;
            $scope.setAvailableRoutes([]);
          });
        }
      };
      setUpRoute();

      window.transitart.on('poloko-reload-route', function(route) {
        $scope.route = route;
        setUpRoute();
      });

      $scope.$parent.changeInput = function () {
        $analytics.eventTrack('route-search-change-input', {category: 'route-search'});
        setUpRoute();
      };

      $scope.$parent.changeTime = function () {
        $analytics.eventTrack('route-search-change-time', {category: 'route-search'});

        if (moment($scope.route.time, CONFIG.TIME_FORMAT).isValid() && /^([0-9]|0[0-9]|1[0-9]|2[0-3]):[0-5][0-9]$/.test($scope.route.time)) {
          $scope.route.invalidTime = false;
          setUpRoute();
        } else {
          $scope.route.invalidTime = true;
        }
      };
      $scope.$on('route-search-change-time', $scope.$parent.changeTime);

      $scope.toggled = {index: undefined};

      $scope.$on("$destroy", function () {
        $scope.mapData = {};
        $scope.mapData.markers = {};
        $scope.mapData.paths = {};
        $scope.availableRoutes = undefined;
      });
      PageService.setMetaTags();
    }]);

}());
