(function () {
  

  var services = angular.module('poloko.services');
  services.service('polokoHttp', ['$q', '$state', '$http', 'ipCookie', 'errors', 'PATHS', 'CONFIG', 'MODEL',
    function ($q, $state, $http, ipCookie, errors, PATHS, CONFIG, MODEL) {

      var _processRequest = function (url, method, data, params) {
        if (!params) {
          params = {};
        }

        params.key = angular.element('#poloko-module').attr('api-key') ||
          angular.element('#poloko-module').attr('data-api-key') || MODEL.API_KEY;
        window.transitart.modifyRequest({data: data, state: {name: $state.current.name, params: $state.params}});


        window.transitart.fire('poloko-request-send', {
          requestMethod: method,
          requestParams: params,
          requestData: data,
          requestUrl: url
        });

        return $http({
          method: method,
          url: url,
          data: data,
          params: params
        }).then(function (response) {

          window.transitart.fire('poloko-response-get', {
            responseData: response.data,
            responseStatus: response.status,
            responseStatusText: response.statusText,
            responseUrl: response.config.url,
            responseMethod: response.config.method
          });

          return response.data;
        });
      };

      var get = function (url, data) {
        return _processRequest(url, 'GET', data);
      };

      var post = function (url, data) {
        return _processRequest(url, 'POST', data);
      };

      var request = function (obj) {
        return _processRequest(obj.url, obj.method, obj.data, obj.params);
      };

      return {
        post: post,
        get: get,
        request: request
      };

    }]);

}());
